import React, { Fragment,useState } from "react";
import { Form, Icon, Grid } from "semantic-ui-react";
import "./App.css";

function App() {
  const [items , setItems] = useState([])


  const addTask = (e) => {
    e.preventDefault();
    console.log(items);

    const newItem ={
      text : e.target.name,
      key : new Date()
    }
    setItems(previousState => {
      console.log(previousState);
      previousState.push(newItem);
      // console.log()
    })
    console.log('Form is submitted');
  }


  return (
    <Fragment>
      <nav>
        <div className="logo">
          <h2>TodoList</h2>
        </div>
      </nav>
      <div className="bf">
        <Grid textAlign="center" >
          <Grid.Column className="grid-width" >
            <Form onSubmit={addTask} className="form"  size="large">
              <Form.Input name="task"  placeholder="Add Task" required/>
              <Form.Button  color="green" inverted >
                <Icon name="checkmark"/>Add Task
              </Form.Button>
            </Form>
          </Grid.Column>
        </Grid>
      </div>
    </Fragment>
  );
}

export default App;
