import React, { Fragment,useState } from "react";
import { Form, Icon, Grid } from "semantic-ui-react";
import TodoItems from './TodoItems/TodoItems';
import {Button , Nav} from 'react-bootstrap'
import "./App.css";

function App() {
  const [items , setItems] = useState([])
  const [val , setVal] = useState('');


  const addTask = (e) => {
    e.preventDefault();

    const newItem ={
      text : val,
      key : new Date()
    }
    setItems(items.concat(newItem));
    console.log('Form is submitted');
    setVal("");
  }
  const inputChangeHandler = (e) => {
    setVal(e.target.value);
  }


  return (
    <Fragment>
      <nav class="navbar navbar-expand-md navbar-dark primary-color ">
  <a class="navbar-brand" href="#">Navbar</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav"
    aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNav">
    <ul class="navbar-nav ml-auto">
      <li class="nav-item active">
        <a class="nav-link" href="#">Home </a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="#">Features</a>
      </li>
      <li class="nav-item">
        <a class="nav-link" href="#">Pricing</a>
      </li>
      <li class="nav-item">
        <a class="nav-link disabled" href="#">Disabled</a>
      </li>
    </ul>
  </div>
</nav>
      {/* <nav>
        <div className="logo">
          <h2>TodoList </h2>
        </div>
        <ul>
            <li>
              <a>Something</a>
            </li>
          </ul>
      </nav>
      <Button >Something</Button> */}
      <div className="bf">
        <Grid textAlign="center" >
          <Grid.Column className="grid-width" >
            <Form onSubmit={addTask} className="form"  size="large">
              <Form.Input name="task" value={val}  placeholder="Add Task" onChange={(e)=>inputChangeHandler(e)} required/>
              <Form.Button  color="green" inverted >
                <Icon name="checkmark"/>Add Task
              </Form.Button>
            </Form>
          </Grid.Column>
        </Grid>
        <TodoItems entries={items}/>
      </div>
    </Fragment>
  );
}

export default App;
