import React, { Fragment,useState ,useEffect } from "react";
import { Form, Icon, Grid } from "semantic-ui-react";
import TodoItems from './TodoItems/TodoItems';
import "./App.css";


function App() {
  const [items , setItems] = useState([])
  const [val , setVal] = useState('');
  useEffect(()=>{
    loadTasks();
  } ,[])

  const loadTasks = () => {
    fetch('/tasks/getTasks' ,{method : 'GET' , headers : {
      'Content-Type': 'application/json'
    }}).then(res => {
      return res.json()
    }).then(resData => {
      let newItems = [...items];
      newItems =  resData.tasks.map(t => t);
      setItems(newItems);
    })
  }

  const inputChangeHandler = (e) => {
    setVal(e.target.value);
  }

  const createTask = (e) => {
    e.preventDefault();
    if(val !== ""){
      fetch('/tasks/add' ,  {method : 'POST' ,headers : {
        'Content-Type': 'application/json'
      }, body : JSON.stringify({
        text : val
      })}).then(res => {
        return res.json();
      }).then(resData => {
          const newItem ={
            taskText : resData.task.taskText,
            key : resData.task._id
          }
          let updatedItems = [...items];
          updatedItems.push(newItem);
          setItems(updatedItems);
          setVal("");
      }).catch(err=>{
        console.log(err)
      })
    }
  }

  const deleteItem = (e,taskId) =>{
    // e.preventDefault();
    console.log(taskId);
    // fetch("/tasks/delete/" + taskId ,
    //   {
    //     method : 'DELETE' 
    //   }).then(res => {

    //     return res.json()
    //   }).then(result => {
    //     console.log(result.message);
    //     const index = items.findIndex(task => task._id === taskId);
    //     console.log('index of deleted task is : ' + index);
    //     const newItems = [...items];
    //     const updatedItems = newItems.filter(item => item._id !== taskId);
    //     console.log(updatedItems);
    //     setItems(updatedItems);
    //   })
  }


  return (
    <Fragment>


      <nav>
        <div className="logo">
          <h2 className="so">TodoList </h2>
          <ul className="left">

          </ul>
        </div>

      </nav>
      <div className="bf">
        <Grid textAlign="center" >
          <Grid.Column className="grid-width" >
            <Form onSubmit={createTask} className="form"  size="large">
              <Form.Input name="task" value={val}  placeholder="Add Task" onChange={(e)=>inputChangeHandler(e)} required/>
              <Form.Button  color="green" inverted onClick={createTask}>
                <Icon name="checkmark"/>Add Task
              </Form.Button>
            </Form>
          </Grid.Column>
        </Grid>
        <TodoItems entries={items} delete={deleteItem}/>
      </div>
    </Fragment>
  );
}

export default App;
