import React, { Fragment,useState } from "react";
import { Form, Icon, Grid } from "semantic-ui-react";
import TodoItems from './TodoItems/TodoItems';
import "./App.css";

function App() {
  const [items , setItems] = useState([])
  const [val , setVal] = useState('');


  const addTask = (e) => {
    e.preventDefault();

    const newItem ={
      text : val,
      key : new Date()
    }
    setItems(items.concat(newItem));
    console.log('Form is submitted');
    setVal("");
  }
  const inputChangeHandler = (e) => {
    setVal(e.target.value);
  }


  return (
    <Fragment>
      <nav>
        <div className="logo">
          <h2 className="so">TodoList </h2>
        </div>
        <ul className="left">
            <li>Somehting</li>
          </ul>
      </nav>
      <div className="bf">
        <Grid textAlign="center" >
          <Grid.Column className="grid-width" >
            <Form onSubmit={addTask} className="form"  size="large">
              <Form.Input name="task" value={val}  placeholder="Add Task" onChange={(e)=>inputChangeHandler(e)} required/>
              <Form.Button  color="green" inverted >
                <Icon name="checkmark"/>Add Task
              </Form.Button>
            </Form>
          </Grid.Column>
        </Grid>
        <TodoItems entries={items}/>
      </div>
    </Fragment>
  );
}

export default App;
