import React, { Fragment,useState } from "react";
import { Form, Icon, Grid } from "semantic-ui-react";
import TodoItems from './TodoItems/TodoItems';
import {Button , Nav} from 'react-bootstrap'
import "./App.css";

function App() {
  const [items , setItems] = useState([])
  const [val , setVal] = useState('');


  const addTask = (e) => {
    e.preventDefault();

    const newItem ={
      text : val,
      key : new Date()
    }
    setItems(items.concat(newItem));
    console.log('Form is submitted');
    setVal("");
  }
  const inputChangeHandler = (e) => {
    setVal(e.target.value);
  }


  return (
    <Fragment>
      <nav className="navbar navbar-expand-md  nav">
        <a className="navbar-brand" href="/" className="links nav-brand">Todolist</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ml-auto">
            <li className="nav-item active">
              <a className="nav-link" href="#">Home <span className="sr-only">(current)</span></a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#">Features</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#">Pricing</a>
            </li>
            <li className="nav-item">
              <a className="nav-link disabled" href="#">Disabled</a>
            </li>
          </ul>
        </div>  
      </nav>
      {/* <nav>
        <div className="logo">
          <h2>TodoList </h2>
        </div>
        <ul>
            <li>
              <a>Something</a>
            </li>
          </ul>
      </nav>
      <Button >Something</Button> */}
      <div className="bf">
        <Grid textAlign="center" >
          <Grid.Column className="grid-width" >
            <Form onSubmit={addTask} className="form"  size="large">
              <Form.Input name="task" value={val}  placeholder="Add Task" onChange={(e)=>inputChangeHandler(e)} required/>
              <Form.Button  color="green" inverted >
                <Icon name="checkmark"/>Add Task
              </Form.Button>
            </Form>
          </Grid.Column>
        </Grid>
        <TodoItems entries={items}/>
      </div>
    </Fragment>
  );
}

export default App;
